class Solver {
public:
    Solver(int n) {
        this->N = n;
    }

    bool solve() {
        N /= 10;

        res.resize(4);
        cin >> a >> b >> c >> d;
        REP(l, d + 1) {
            REP(k, c + 1) {
                REP(j, b + 1) {
                    REP(i, a + 1) {
                        int val = i + 5 * j + 10 * k + 50 * l;
                        if (val < N) continue;
                        int ret = change(val);
                        ret += a - i + b - j + c - k + d - l;
                        if (tmp > ret) {
                            tmp = ret;
                            res[0] = i;
                            res[1] = j;
                            res[2] = k;
                            res[3] = l;
                        }
                    }
                }
            }
        }
        REP(i, 4) {
            if (res[i]) {
                cout << coins[i] << " " << res[i] << endl;
            }
        }

        return true;
    }

private:
    int N;
    int a, b, c, d;
    int tmp = INF;

    V<int> res;
    V<int> coins = {10, 50, 100, 500};

    int change(int x) {
        int ret = 0;
        int chg = x - N;
        ret += chg / 50;
        chg %= 50;
        ret += chg / 10;
        chg %= 10;
        ret += chg / 5;
        chg %= 5;
        ret += chg;

        return ret;
    }
};

