int main()
{
	int N;
	while (cin >> N && N != 0)
	{
		int kind[4] = {};
		int kindSum = 0;
		for (int i = 0; i < 4; i++)
		{
			cin >> kind[i];
			kindSum += kind[i];
		}

		int min = 100000;
		int ans = 0;
		for (int i = 0; i <= kind[0]; i++)
		{
			int ii = i * 10;
			if (ii > N)continue;
			for (int j = 0; j <= kind[1]; j++)
			{
				int jj = j * 50;
				for (int k = 0; k <= kind[2]; k++)
				{
					int kk = k * 100;
					for (int l = 0; l <= kind[3]; l++)
					{
						int ll = l * 500;
						int payMoney = kk + ll + jj + ii;
						int diff = N - payMoney;
						if (diff > 0)continue;

						diff = -diff;
						int numberOfCoin = diff / 500;
						int a = diff % 500;
						numberOfCoin += a / 100;
						a = a % 100;
						numberOfCoin += a / 50;
						a = a % 50;
						numberOfCoin += a / 10;
						if (numberOfCoin < kindSum)
						{
							kindSum = numberOfCoin;
							ans = payMoney;
						}


					}
				}
			}
		}

		kind[3] = ans / 500;
		ans = ans % 500;
		kind[2] = ans / 100;
		ans = ans % 100;
		kind[1] = ans / 50;
		ans = ans % 50;
		kind[0] = ans / 10;

		if (kind[0] != 0)
		{
			cout << 10 << " "<<kind[0] << endl;
		}

		if (kind[1] != 0)
		{
			cout << 50 << " " << kind[1] << endl;
		}

		if (kind[2] != 0)
		{
			cout << 100 << " " << kind[2] << endl;
		}

		if (kind[3] != 0)
		{
			cout << 500 << " " << kind[3] << endl;
		}
	}
	return 0;
}

