int mfdfs(int curr, int goal, int min_flow, int used[105], int g[105][105], int n){
    cout << "dfs" << endl;
    if(curr == goal) return min_flow;
    REP(next, n){
        if(g[curr][next] && !used[next]){
            used[next] = 1;
            int flow = mfdfs(next, goal, min(min_flow, g[curr][next]), used, g, n);
            if(flow){
                g[curr][next] -= flow;
                g[next][curr] += flow;
                return flow;
            }
        }
    }
    return 0;
}

int max_flow(int g[105][105], int n, int s, int t){
    int flow = 0;
    while(true){
        int used[105];
        REP(i, n) used[i] = 0;
        used[s] = 1;
        int tmp_flow = mfdfs(s, t, INF, used, g, n);
        if(tmp_flow == 0) return flow;
        flow += tmp_flow;
    }
}

int n, c[4], v[4]={10,50,100,500}, ans[4], minc = INF, flag = 0;

int ccount(int p, int coins[4]){
    int cnt = 0;
    RREP(i,4){
        int t = p / v[i];
        cnt += t;
        if(coins[i] && t) return INF;
        p -= v[i] * t;
    }
    return cnt;
}

int dfs(int cnt, int s, int p, int coins[4]){
    // cout << "dfs " << cnt << " " << s << " " << p << endl;
    if(p == 4){
        if(s >= n){
            cnt += ccount(s-n, coins);
            if(minc > cnt){
                minc = cnt;
                cp(coins, ans, 4);
            }
            return cnt;
        }
        else return INF;
    }
    int m = INF;
    REP(i, c[p]+1){
        coins[p] = i;
        m = min(m, dfs(cnt + i, s + v[p]*i, p + 1, coins));
    }
    return m;
}

