int calcOtsuri(int cost,int money){
    int leftMoney=money-cost;
    int m[4]={10,50,100,500};
    int cnt=0;
    for(int i = 3; i >= 0; i--){
        cnt+=leftMoney/m[i];
        leftMoney-=(leftMoney/m[i])*m[i];
    }
    return cnt;
}

