using namespace std;

int s[4]={10,50,100,500};
int n,t[4],u[4],maxm,ans[4];

int calc(int x){
  int res=0;
  for(int i=3;i>=0;i--){
    res+=x/s[i];
    x%=s[i];
  }
  return res;
}

void solve(int pos,int cnt,int sum){
  if(pos==4){
    if(sum<n)return;
    cnt-=calc(sum-n);
    if(cnt<=maxm)return;
    for(int i=0;i<4;i++)ans[i]=u[i];
    maxm=cnt;
    return;
  }
  for(int i=0;i<=t[pos];i++){
    u[pos]=i;
    solve(pos+1,cnt+i,sum+i*s[pos]);
  }
}

