	FOR(i, 2, n)
	{
		if (!isPrime[i]) continue;
		primes.push_back(i);
		for (int j = i * 2; j <= n; j += i)
			isPrime[j] = false;
	}
}

/// template end ///

void solve()
{
	REP (testcase, INF)
	{
		READ(int, price);
		if (!price) { break; }
		if (testcase != 0) { cout << endl; }

		READ(int, x10, x50, x100, x500);
		int money = x10 * 10 + x50 * 50 + x100 * 100 + x500 * 500;
		int change = money - price;

		int c500 = change / 500; change %= 500;
		int c100 = change / 100; change %= 100;
		int c50 = change / 50; change %= 50;
		int c10 = change / 10; change %= 10;

		x500 = max(x500 - c500, 0);
		x100 = max(x100 - c100, 0);
		x50 = max(x50 - c50, 0);
		x10 = max(x10 - c10, 0);

		if (x10) { write(10, x10); }
		if (x50) { write(50, x50); }
		if (x100) { write(100, x100); }
		if (x500) { write(500, x500); }
	}
}

