inline int minimumExchange(int m, int *nums)
{
	int t = 0;
	int ans = 0;

	t = m / 500;
	m -= 500 * t;
	nums[3] = t;
	ans += t;

	t = m / 100;
	m -= 100 * t;
	nums[2] = t;
	ans += t;

	t = m / 50;
	m -= 50 * t;
	nums[1] = t;
	ans += t;

	t = m / 10;
	m -= 10 * t;
	nums[0] = t;
	ans += t;

	return ans;
}

