		if( P == 0 ) break;

		if( cnt > 0 ) cout << endl;

		cin >> c[0] >> c[1] >> c[2] >> c[3];

		//
		for(int i = 0; i <= c[0]; i++) {
			for(int j = 0; j <= c[1]; j++) {
				for(int k = 0; k<= c[2]; k++) {
					for(int l = 0; l <= c[3]; l++) {
						if( P <= i * 10 + j * 50 + k * 100 + l * 500 ) {
							pay np;
							np.c[0] = i;
							np.c[1] = j;
							np.c[2] = k;
							np.c[3] = l;
							np.num = i + j + k + l;
							np.sum = i * 10 + j * 50 + k * 100 + l * 500;
							p.push_back( np );
						}
					}
				}
			}
		}

