array<int, 4> the_change(int n) {
    if(n <= 0) return {{0, 0, 0, 0}};

    array<int, 4> ret;
    ret[3] = n / 500, n %= 500;
    ret[2] = n / 100, n %= 100;
    ret[1] = n / 50, n %= 50;
    ret[0] = n / 10;
    return ret;
}

int main() {
    int N;
    bool is_first = true;
    while(cin >> N, N) {
        if(!is_first) {
            cout << endl;
        }
        is_first = false;

        array<int, 4> coin; // 10, 50, 100, 500
        for(int i=0; i<4; ++i) {
            cin >> coin[i];
        }

        int num = 1e9;
        array<int, 4> ret;
        for(int i=0; i<=coin[0]; ++i) { // 20^4
            for(int j=0; j<=coin[1]; ++j) {
                for(int k=0; k<=coin[2]; ++k) {
                    for(int l=0; l<=coin[3]; ++l) {
                        if(N <= i*10 + j*50 + k*100 + l*500) {
                            auto change = the_change(i*10 + j*50 + k*100 + l*500 - N);
                            array<int, 4> pay = {i, j, k, l};
                            int rest = accumulate(coin.begin(), coin.end(), 0);
                            for(int m=0; m<change.size(); ++m) {
                                int tmp = min(change[m], pay[m]);
                                change[m] -= tmp;
                                pay[m] -= tmp;
                                rest += change[m] - pay[m];
                            }
                            if(rest < num) {
                                for(int m=0; m<4; ++m) {
                                    ret[m] = pay[m];
                                }
                                num = rest;
                            }
                        }
                    }
                }
            }
        }
        array<int, 4> tmp2 = {10, 50, 100, 500};
        for(int i=0; i<4; ++i) {
            if(ret[i] != 0) {
                cout << tmp2[i] << " " << ret[i] << endl;
            }
        }
    }
}

