int num[6];

int cash(int a){
	int t_num[6];
	int ret = 0;
	for(int i=0;i<6;i++){
		t_num[i] = num[i];
	}
	for(int i=5;i>=0;i--){
		if(a>=item[i] && t_num[i]){
			a-=item[i];
			t_num[i]--;
			ret++;
			i++;
		}
	}
	if(a!=0)return -1;
	return ret;
}

int plz(int a){
	int ret = 0;
	for(int i=5;i>=0;i--){
		if(a>=item[i]){
			a-=item[i];
			i++;
			ret++;
		}
	}
	return ret;
}

