            ten -= m / 10 % 5;
            m -= (m / 10 % 5) * 10;
        }
        while (ten >= 5) // && m >= 50
        {
            get<0>(coin) += 5;
            ten -= 5;
            m -= 50;
        }
        if (m % 100 <= 50 && m % 100 != 0 && fifty >= 1)
        {
