            fifty--;
            m -= 50;
        }
        while (fifty >= 2)
        {
            get<1>(coin) += 2;
            fifty -= 2;
            m -= 100;
        }
        if (m % 100 != 0 && one_hundred >= 1)
        {
            get<2>(coin) += 1;
            one_hundred--;
            //            m -= 100;
            m = (m / 100) * 100;
        }
        if (m / 100 % 5 <= one_hundred && m / 100 % 5 != 0)
        {
            get<2>(coin) += m / 100 % 5;
            one_hundred -= m / 100 % 5;
            m -= get<2>(coin) * 100;
