  int num500yen = (sum >= 500 ? sum/500 : 0);
  sum %= 500;

  int num100yen = (sum >= 100 ? sum/100 : 0);
  sum %= 100;

  int num50yen = (sum >= 50 ? sum/50 : 0);
  sum %= 50;

  int num10yen = (sum >= 10 ? sum/10 : 0);
  sum %= 10;

  Coin res = {num10yen,num50yen,num100yen,num500yen};
  return res;
}

