const int INF = (1 << 29);

int getCoin(int x, int i, int j, int k, int m)
{
  int r = 0;
  while (x >= 500) {
    x -= 500, r++;
    if (i) return INF;
  }
  while (x >= 100) {
    x -= 100, r++;
    if (j) return INF;
  }
  while (x >= 50) {
    x -= 50, r++;
    if (k) return INF;
  }
  while (x >= 10) {
    x -= 10, r++;
    if (m) return INF;
  }
  return r;
}

