using namespace std;

class coin{
public:
	int n,a,b,c,d,rest,sum;
	coin(int _n,int _a,int _b,int _c,int _d,int _sum){
		n=_n;
		a=_a;
		b=_b;
		c=_c;
		d=_d;
		sum=_sum;
		rest=10*a+50*b+100*c+500*d-n;
	};

	int calc(){

		int res=sum-a-b-c-d;

		rest=10*a+50*b+100*c+500*d-n;

		if(d!=0 && rest/500!=0) return sum;
		res+=rest/500;
		rest%=500;
		if(c!=0 && rest/100!=0) return sum;
		res+=rest/100;
		rest%=100;
		if(b!=0 && rest/50!=0) return sum;
		res+=rest/50;
		rest%=50;
		if(a!=0 && rest/10!=0) return sum;
		res+=rest/10;

		return res;
	}
};


int main()
{
	int n,a,b,c,d;
	while(cin>>n){
		if(n==0) break;
		cin>>a>>b>>c>>d;
		int sum=a+b+c+d;
		coin ans = coin(n,a,b,c,d,sum);

		for(int aa=0;aa<=a;aa++){
			for(int bb=0;bb<=b;bb++){
				for(int cc=0;cc<=c;cc++){
					for(int dd=0;dd<=d;dd++){
						coin tmp = coin(n,aa,bb,cc,dd,sum);
						if(tmp.rest>=0 && ans.calc()>tmp.calc()) ans=tmp;
					}
				}
			}
		}
		if(ans.a!=0) cout<<10<<" "<<ans.a<<endl;
		if(ans.b!=0) cout<<50<<" "<<ans.b<<endl;
		if(ans.c!=0) cout<<100<<" "<<ans.c<<endl;
		if(ans.d!=0) cout<<500<<" "<<ans.d<<endl;
//		cout<<"->"<<ans.calc()<<endl;
		cout<<endl;
	}


	return 0;
}

