void cal(int c, int *pcost, int *pnum, int *pyen, const int *pcoin)
{
    int div = *pcost % pcoin[c+1];
    if(div < pcoin[c] * pyen[c])
    {
        pnum[c] += div / pcoin[c];
        pyen[c] -= div / pcoin[c];
        pcost -= div;
    }
    else
    {
        int a = (pcoin[c+1]-div) / pcoin[c];
        pyen += a;
        pcost += pcoin[c+1]-div;
    }
}


