#define REP(i, j) for(int i = 0; i < (int)(j); ++i)
#define FOR(i, j, k) for(int i = (int)(j); i < (int)(k); ++i)
#define SORT(v) sort((v).begin(), (v).end())
#define REVERSE(v) reverse((v).begin(), (v).end())
typedef complex<double> P;
const int T = 4;
const int INF = 1e9;

int N, ans, c[4];
vector<int> ans_v;

int check(vector<int> &v, vector<int> &used, int sum){
  int tmp = 0;
  REP(i, T) tmp += v[i];
  sum -= N;
  vector<int> r(T, 0);
  for(int i = T - 1; i >= 0; --i){
    r[i] = sum / c[i];
    sum %= c[i];
    if(used[i] > 0 && r[i] > 0) return -1;
    tmp += r[i];
  }
  if(tmp < ans){
    ans = tmp;
    ans_v = used;
  }
  return tmp;
}

void dfs(vector<int> &v, vector<int> &used, int sum, set< vector<int> > &S, map<int, int> &closed){
  //cout <<"==========" <<endl;
  //cout <<sum <<endl;
  //REP(i, T) cout <<v[i] <<", "; cout <<endl;
  //REP(i, T) cout <<used[i] <<", "; cout <<endl;
  int f = -1;
  if(sum >= N) f = check(v, used, sum);
  if(f != -1 && closed.find(sum) != closed.end() && closed[sum] <= f) return ;
  closed[sum] = f;
  REP(i, T){
    if(!v[i]) continue;
    v[i] -= 1;
    used[i] += 1;
    if(S.find(used) == S.end()){
      S.insert(used);
      dfs(v, used, sum + c[i], S, closed);
    }
    v[i] += 1;
    used[i] -= 1;
  }
}

int main() {
  c[0] = 10; c[1] = 50; c[2] = 100; c[3] = 500;
  bool f = false;
  while(cin >>N && N){
    if(f) cout <<endl;
    f = true;
    ans = INF;
    vector<int> v(T), used(T, 0);
    REP(i, T) cin >>v[i];
    set< vector<int> > S;
    map<int, int> closed;
    dfs(v, used, 0, S, closed);
    REP(i, T) if(ans_v[i]) cout <<c[i] <<" " <<ans_v[i] <<endl;
  }
  return 0;
}

