int solve(int m, int i, int j, int k, int l) {
  int total = i * 10 + j * 50 + k * 100 + l * 500; 
  if( total < m ) return -1;
  int diff = total - m;
  int res = 0;
  res += (diff / 10 % 10) % 5 + (diff / 10 % 10) / 5;
  res += (diff / 100 % 10) % 5 + (diff / 100) / 5;
  return res;
}


