const int INF = 1000000000;

int main(){
	bool first = true;
	while(true){
		int x;
		cin >> x;
		if(x == 0){ break; }
		if(!first){ cout << endl; }
		first = false;
		int a, b, c, d;
		cin >> a >> b >> c >> d;
		int answer = INF;
		int aa = 0, ab = 0, ac = 0, ad = 0;
		for(int i = 0; i <= a; ++i){
			for(int j = 0; j <= b; ++j){
				for(int k = 0; k <= c; ++k){
					for(int l = 0; l <= d; ++l){
						int s = (i * 10 + j * 50 + k * 100 + l * 500);
						if(s < x){ continue; }
						int r = s - x;
						if(l > 0 && r >= 500){ continue; }
						int rd = r / 500;
						r %= 500;
						if(k > 0 && r >= 100){ continue; }
						int rc = r / 100;
						r %= 100;
						if(j > 0 && r >= 50){ continue; }
						int rb = r / 50;
						r %= 50;
						if(i > 0 && r >= 10){ continue; }
						int ra = r / 10;
						r %= 10;
						int result =
							(a - i) + (b - j) + (c - k) + (d - l) +
							ra + rb + rc + rd;
						if(result < answer){
							answer = result;
							aa = i;
							ab = j;
							ac = k;
							ad = l;
						}
					}
				}
			}
		}
		if(aa > 0){ cout << "10 " << aa << endl; }
		if(ab > 0){ cout << "50 " << ab << endl; }
		if(ac > 0){ cout << "100 " << ac << endl; }
		if(ad > 0){ cout << "500 " << ad << endl; }
	}
	return 0;
}

