    while(1){
        if(c == 0){
            break;
        }
        if(500 <= c){  // c  =600
            c-=500;
            cnt++;
        }else if(100 <= c){
            c-=100;
            cnt++;
        }else if(50 <= c){
            c-=50;
            cnt++;
