using namespace std;

int main()
{
    const int price[4] = {10, 50, 100, 500};
    int cost;
    cin >> cost;
    int posses_num[4];
    for(int i = 0; i < 4; i++){
        cin >> posses_num[i];
    }

    int pay_num[4];
    int pay_price;
    int back_price;
    int back_num[4];
    int paid_num;
    int min_num = -1000000;
    int min_nums[4];

    for(pay_num[3] = posses_num[3]; pay_num[3] >= 0; pay_num[3]--){
        for(pay_num[2] = posses_num[2]; pay_num[2] >= 0; pay_num[2]--){
            for(pay_num[1] = posses_num[1]; pay_num[1] >= 0; pay_num[1]--){
                for(pay_num[0] = posses_num[0]; pay_num[0] >= 0; pay_num[0]--){
                    pay_price = 0;
                    for(int i = 0; i < 4; i++){
                        pay_price += price[i] * pay_num[i];
                    }
                    if(pay_price < cost)break;
                    back_price = pay_price - cost;
                    paid_num = 0;
                    for(int i = 3; i >= 0; i--){
                        back_num[i] = back_price / price[i];
                        paid_num += pay_num[i] - back_num[i];
                        back_price %= price[i];
                    }
                    if(paid_num >= min_num){
                        min_num = paid_num;
                        for(int i = 0; i < 4; i++){
                            min_nums[i] = pay_num[i];
                        }
                    }
                }
            }
        }
    }

    for(int i = 0; i < 4; i++){
        if(min_nums[i]){
            cout << price[i] << " " << min_nums[i] << endl;
        }
    }

    return 0;
}

