int retcoin(int money, int a)
{
	money -= a;
	int cnt = 0;
	if (500 <= money){
		cnt += (money / 500);
		money %= 500;
	}
	if (100 <= money) {
		cnt += (money / 100);
		money %= 100;
	}
	if (50 <= money){
		cnt += (money / 50);
		money %= 50;
	}
	cnt += (money / 10);

	return cnt;
}

