#define REP(i,n) for(int i=0;i<(n);i++)

int main(){
	int n;
	int price[4] = {10,50,100,500};
	while(cin>>n,n){
		vector<int> coin(4);

		for(int i=0;i<4;i++){
			cin >> coin[i];
		}
		vector<int> min_coins(4,0);
		int min_ct = 100000;
		REP(i,coin[3]+1){
			REP(j,coin[2]+1){
				REP(k,coin[1]+1){
					REP(l,coin[0]+1){
						int ct = 0;
						int sum = i*price[3] + j*price[2] + k*price[1] + l*price[0];
						if(sum < n)continue;
						else{
							int change = sum - n;
							for(int p = 3;p>=0;p--){
								int change_ct = 0;
								while(change >= price[p]){
									change_ct++;
									change -= price[p];
								}
								ct += change_ct;
							}
							ct += i + j + k + l;
							if(ct < min_ct){
								min_coins[0] = l;min_coins[1] = k;min_coins[2] = j;min_coins[3] = i;
								min_ct = ct;
							}
						}
					}
				}
			}
		}
		for(int i=0;i<4;i++){
			if(min_coins[i]){
				cout << price[i] << " " << min_coins[i] << endl;
			}
		}
		cout << '\n';
	}

}

