    int tmp = m / coin[i];
    if(tmp > 0 && d[i] > 0)
      return 100000;
    ret += tmp;
    m = m % coin[i];
  }
  return ret;
}

void solve(int i, int m, int cnt){
  //printf("solve(%d,%d,%d)\n",i,m,cnt);
  //REP(j,i) printf("d[%d]=%d ",j,d[j]); puts("");
  if(i == 4){
