    //printf("sum: %d\n",sum);
    if(ans < sum){
      ans = sum;
      REP(i,4)
        a[i] = d[i];
    }
    return;
  }

  if(i < 3){
    REP(j,c[i]+1){
      d[i] = j;
      solve(i+1, m - d[i] * coin[i], cnt + d[i]);
    }
  }else{
    if(m <= 0){
      d[i] = 0;
      solve(i+1, m, cnt);
    }else{
      d[i] = (m + 499) / 500;
      if(d[i] <= c[i])
        solve(i+1, m - d[i] * coin[i], cnt + d[i]);
    }
  }
  return;
}

