    }

    sum -= pay;
    for (int i = 3; i >= 0; --i) {
      int n = sum / cof[i];
      if (n > 0) {
	c[i] -= n;
	sum -= cof[i]*n;
      }
    }

    for (int i = 0; i < 4; ++i) {
      if (c[i] > 0)
	printf("%d %d\n", cof[i], c[i]);
    }
  }
  return 0;
}

