#define int ll

#define FOR(i,a,b) for(int i=int(a);i<int(b);i++)
#define REP(i,b) FOR(i,0,b)

int read(){
	int i;
	scanf("%lld",&i);
	return i;
}

signed main(){
	while(true){
		int total=read();
		if(total==0) break;
                cout << endl;
		int coinNum[4];
		int coin[4]={10,50,100,500};
		REP(i,4){
			coinNum[i]=read();
		}
		int pay;
		int totalCoinNum;
		int ans[5]={1000000,0,0,0,0};
		REP(i,coinNum[0]+1){
			REP(j,coinNum[1]+1){
				REP(k,coinNum[2]+1){
					REP(l,coinNum[3]+1){
						pay = coin[0]*i+coin[1]*j+coin[2]*k+coin[3]*l;
						if(total>pay) continue;
						int oturi = pay-total;
						totalCoinNum=coinNum[0]+coinNum[1]+coinNum[2]+coinNum[3]-(i+j+k+l);
						REP(i,4){
							int cnt = oturi/coin[3-i];
							totalCoinNum+=cnt;
							oturi%=coin[3-i];
						}
						if(totalCoinNum<ans[0]){
							ans[0] = totalCoinNum;
							ans[1] = i;
							ans[2] = j;
							ans[3] = k;
							ans[4] = l;
						}
					}
				}
			}
		}
		if(ans[1]!=0) cout << "10 " << ans[1] << endl;
		if(ans[2]!=0) cout << "50 " << ans[2] << endl;
		if(ans[3]!=0) cout << "100 " << ans[3] << endl;
		if(ans[4]!=0) cout << "500 " << ans[4] << endl;
	}
}

