int countOfChargeCoin(int charge){
  int count=0;
  count+=charge/500;
  charge%=500;
  count+=charge/100;
  charge%=100;
  count+=charge/50;
  charge%=50;
  count+=charge/10;
  return count;
}

void solve(){
  int m=0;
  int mina,minb,minc,mind;
  REP(i,a+1)REP(j,b+1)REP(k,c+1)REP(l,d+1){
    int charge=i*10+j*50+k*100+l*500-n;
    if(charge<0)continue;
    int tmpm=countOfChargeCoin(charge)-a-b-c-d;
    if(m>tmpm){
      m=tmpm;
      mina=i;
      minb=j;
      minc=k;
      mind=l;
    }
  }
  if(mina>0)cout<<"10 "<<mina<<endl;
  if(minb>0)cout<<"50 "<<minb<<endl;
  if(minc>0)cout<<"100 "<<minc<<endl;
  if(mind>0)cout<<"500 "<<mind<<endl;
}

