#include <iostream>

using namespace std;

int unit[4] = {10, 50, 100, 500};

int coins(int n){
	int ret=0;
	ret+=n/500,n%=500;
	ret+=n/100,n%=100;
	ret+=n/50,n%=50;
	ret+=n/10,n%10;
	return ret;
}

int main(){
	int n;
	for(int i=0;cin >> n,n;i++){
		int d[4],min=160000;
		int ret[4] = {20,20,20,20};
		if(i != 0) cout << endl;
		for(int i=0;i<4;i++) cin >> d[i];
		for(int i=0;i<=d[0];i++){
			for(int j=0;j<=d[1];j++){
				for(int k=0;k<=d[2];k++){
					for(int l=0;l<=d[3];l++){
						if(i*10 + j*50 + k*100 + l*500 < n) continue;
						int res = d[0] - i + d[1] - j + d[2] - k + d[3] - l + coins(i*10 + j*50 + k*100 + l*500 - n);
						if(res < min){
							ret[0] = i,ret[1] = j,ret[2] = k,ret[3] = l;
							min = res;
						}
					}
				}
			}
		}
		for(int i=0;i<4;i++){
			if(ret[i] == 0) continue;
			cout << unit[i] << " " << ret[i] << endl;
		}
	}
	return 0;
}

