                                    md -= (md/b[i])*b[i];
                                }
                            }
                            if (minc > t+u) {
                                minc = t+u;
                                a1 = c1; a2 = c2; a3 = c3; a4 = c4;
                            }
                        }
                    }
                }
            }
        }
        if (a1) cout << "10 " << a1 << endl;
        if (a2) cout << "50 " << a2 << endl;
        if (a3) cout << "100 " << a3 << endl;
        if (a4) cout << "500 " << a4 << endl;
