bool check(int cst){
	for(int i=3;i>=0;i--){
		pay[i] = 0;
		while(cst >= money[i]){
			pay[i]++;
			cst -= money[i];
		}
	}
	rep(i,4)if(pay[i] && use[i]) return false;

	return true;
}
void dfs(int pos){
	if(pos == -1){	
		if(cost >= n && check(cost-n)){
			int kans=0;
			rep(i,4)kans += pay[i] + data[i]-use[i];
			if(kans < minans){
				rep(i,4)ans[i] = use[i];
				minans = kans;
			}
		}
		return;
	}
	for(int i=data[pos];i>=0;i--){
		use[pos] = i;
		cost += i * money[pos];
		dfs(pos-1);
		cost -= i * money[pos];
	}
}
