typedef struct{int gh,h,gj,j;} coins;

int c2d(const coins c) {
    return c.gh*500 + c.h*100 + c.gj*50 + c.j*10;
}
coins d2c(int d) {
    return {d/500, (d%500)/100, (d%100)/50, (d%50)/10};
}


int main(void) {
    bool flag = false;
    while(true) {
        int sum;
        scanf("%d", &sum);
        if(!sum) break;

        if(flag) printf("\n");
        flag = true;

        coins in;
        pair<coins, coins> res;
        pair<int, int> min;
        min = pair<int,int>(0xFF,0xFF);

        scanf("%d%d%d%d", &in.j, &in.gj, &in.h, &in.gh);
        for(int gh=0; gh<=in.gh; gh++)
            for(int h=0; h<=in.h; h++)
                for(int gj=0; gj<=in.gj; gj++)
                    for(int j=0; j<=in.j; j++) {
                        if(c2d({gh,h,gj,j}) < sum) continue;
                        coins ret = d2c(c2d({gh,h,gj,j})-sum);

                        if(min.first > (in.gh-gh+ret.gh + in.h-h+ret.h + in.gj-gj+ret.gj + in.j-j+ret.j)) {
                            min.first = (in.gh-gh+ret.gh + in.h-h+ret.h + in.gj-gj+ret.gj + in.j-j+ret.j);
                            min.second= (gh + h + gj + j);
                            res.first = {in.gh-gh+ret.gh , in.h-h+ret.h , in.gj-gj+ret.gj , in.j-j+ret.j};
                            res.second= {gh , h , gj , j};
                        }else if(min.first == (in.gh-gh+ret.gh + in.h-h+ret.h + in.gj-gj+ret.gj + in.j-j+ret.j)){
                            if(min.second< (gh + h + gj + j)) continue;

                            min.first = (in.gh-gh+ret.gh + in.h-h+ret.h + in.gj-gj+ret.gj + in.j-j+ret.j);
                            min.second= (gh + h + gj + j);
                            res.first = {in.gh-gh+ret.gh , in.h-h+ret.h , in.gj-gj+ret.gj , in.j-j+ret.j};
                            res.second= {gh , h , gj , j};
                        }
                    }

        if(res.second.j) printf("10 %d\n", res.second.j);
        if(res.second.gj) printf("50 %d\n", res.second.gj);
        if(res.second.h) printf("100 %d\n", res.second.h);
        if(res.second.gh) printf("500 %d\n", res.second.gh);
    }
    return 0;
}

