#include <iostream>
using namespace std;

int cnt10,cnt50,cnt100,cnt500;
int i,j,k,l,yen,ten,fifty,hundred,five_hundred,
  tmp,result,oturi,oturi_num;
int min10,min50,min100,min500;
int flag = 0;

void check(){
  int min = 100;
  int c10,c50,c100,c500;
  for( i = 0; i <= five_hundred; i++ ){
    for( j = 0; j <= hundred; j++ ){
      for( k = 0; k <= fifty; k++ ){
	for( l = 0; l <= ten; l++ ){
	  result =  10*l + 50*k + 100*j + 500*i; 
	  if( result == yen ){
	    min10 = l; min50 = k; min100 = j; min500 = i;
	    return;
	  }else if( result > yen ){
	    c10 = cnt10 - l;
	    c50 = cnt50 - k;
	    c100 = cnt100 - j;
	    c500 = cnt500 - i;
	    oturi = result - yen;
	    // cout << "oturi" << " " << oturi << endl;
	    if( oturi >= 500 ){
	      tmp = oturi / 500;
	      oturi -= 500 * tmp;
	      c500 += tmp;
	    }

	    if( oturi >= 100 ){
	      tmp = oturi / 100;
	      oturi -= 100 * tmp;
	      c100 += tmp;
	    }

	    if( oturi >= 50 ){
	      tmp = oturi / 50;
	      oturi -= 50 * tmp;
	      c50 += tmp;
	    }

	    if( oturi >= 10 ){
	      c10 += oturi / 10;
	    }

	    oturi_num = c10 + c50 + c100 + c500;

	    if( oturi_num < min ){
	      min = oturi_num;
	      min10 = l;
	      min50 = k;
	      min100 = j;
	      min500 = i;
	    }

	  }

	}
      }
    }
  }

}


int  main(){


  while(1){
    cin >> yen; 
    if( yen == 0 ) break;

    cin >> ten >> fifty >> hundred >> five_hundred;
    cnt10 = ten,cnt50 = fifty,cnt100 = hundred,cnt500 = five_hundred;
    if( flag == 1) cout << endl;

    check();

    if( min10 > 0 ) cout << "10" << " " << min10 << endl;
    if( min50 > 0 ) cout << "50" << " " << min50 << endl;
    if( min100 > 0 ) cout << "100" << " " << min100 << endl;
    if( min500 > 0 ) cout << "500" << " " << min500 << endl;
    flag = 1;
  }
  return 0;
}

