#include<iostream>
using namespace std;

int remi(int cost) {
  int ans = 0;
  ans += cost/500;
  cost %= 500;
  ans += cost/100;
  cost %= 100;
  ans += cost/50;
  cost %= 50;
  ans += cost/10;
  return ans;
}

