        if(m == 0) {
            break;
        }
        int t[4] = {10,50,100,500};
        int c[4]= {},k[4] = {}; 
        rep(i,4) { //10,50,100,500
            cin >> c[i];
        }

        rep(i,3) {
            if(c[i] * t[i] >= m) {
                if(m % t[i] == 0) {
                    k[i] = m/t[i];
                    c[i] -= k[i];
                    m = 0;
                } else {
                    k[i] = m/t[i] + 1;
                    c[i] -= k[i];
                    m = 0;
                }
            } else if(t[i]*c[i] >= m % t[i+1]) {
                k[i] += (m%t[i+1])/t[i];    
                c[i] -= k[i];
                m -= m%t[i+1];
                if(i > 0) {
                    if(c[i] > 0 && m % t[i] != 0) {
                        k[i] += 1;
                        c[i] -= 1;
                        m = (m / t[i]) * t[i];
                    }
                }
                if(c[i] * t[i] >= t[i+1]){
                    int tmp = (c[i]/(t[i+1] / t[i]))*(t[i+1]/t[i]);
                    k[i] += tmp;
                    c[i] -= tmp;
                    m -= tmp*t[i];
                }
            } 
        }

        int bb = 0;
        if(m % t[3] == 0) {
            k[3] = m / t[3];
        } else {
            k[3] = m / t[3] + 1;
            bb = (m/t[3] + 1)*t[3]-m;
        }


        int a[3] = {};
        rep(i,3) {
            if(c[i] * t[i] > t[i+1]) {
                int tmp = ((c[i] / (t[i+1] / t[i])) * (t[i+1] / t[i]));
                k[i] += tmp;
                a[i] = tmp * t[i];  
                c[i] -= tmp;
            } 
            if(i > 0) {
                int tmp = c[i] * t[i];
                int aa = 0; 
                rep(j,i) {
                    aa += a[j];
                }
                if(tmp + aa +bb >= t[i+1]) {
                    k[i] += (t[i+1] - aa -bb) / t[i];
                }
            }
        }

        rep(i,4) {
            if(k[i] != 0) {
                cout << t[i] << " " << k[i] << endl;
            }
        }
        cout << endl;
    }
    return 0;

}

