#include<iostream>
using namespace std;
int coin[]={10,50,100,500};
int n,t[4],u[4],v[4],ans[4],j,sum,sum2,tmp;
int main(){
  sum=-1;
  while(cin>>n&&n){
    if(sum!=-1)cout<<endl;
    for(int i=0;i<4;i++){
      cin>>t[i];
      u[i]=v[i]=ans[i]=0;
    }
    sum=0;
    for(int i=0;i<4;i++){
      while(t[i]>0&&sum<n){
	sum+=coin[i];
	t[i]--;
	ans[i]++;
      }
    }

    sum2=sum-n;
    for(int i=3;i>=0;i--){
      v[i]=u[i]=sum2/coin[i];
      sum2%=coin[i];
    }
    /*
    for(int i=0;i<4;i++){
      cout<<coin[i]<<' '<<ans[i]<<' '<<u[i]<<endl;
    }
    */
    for(int i=0;i<4;i++){
      while(ans[i]>0&&u[i]>0){
	ans[i]--;
	t[i]++;
	u[i]--;
      }
    }

    for(int i=0;i<3;i++){
      while(t[i]*coin[i]>=coin[i+1]&&u[i]==0){
	t[i]-=(coin[i+1]/coin[i]);
	ans[i]+=(coin[i+1]/coin[i]);
      }
    }
    /*
     for(int i=0;i<4;i++){
      cout<<coin[i]<<' '<<ans[i]<<' '<<u[i]<<endl;
    }
    */



    for(int i=0;i<4;i++){
      if(ans[i])cout<<coin[i]<<' '<<ans[i]<<endl;
    }

  }
  return 0;
}

