        int kd = D - d + chd;

        if(n > ka + kb + kc + kd) {
          aa = a, ab = b, ac = c, ad = d;
          n = ka + kb + kc + kd;
        }
      }
    }

    if(!first) cout << endl;
    first = false;

    if(aa > 0) cout << "10 " << aa << endl;
    if(ab > 0) cout << "50 " << ab << endl;
    if(ac > 0) cout << "100 " << ac << endl;
    if(ad > 0) cout << "500 " << ad << endl;
  }

  return 0;
}

