int main(void) {
    int money;
    VI have(4);
    cin >> money;
    while (1) {
        for (int i = 0; i < 4; i++) {
            cin >> have[i];
        }
        VI cash(4, 0);
        VI ret(4, 0);

        money /= 10;
        int max10 = money;
        if (max10 > have[0]) {
            money -= have[0];
            cash[0] += have[0];
            have[0] -= have[0];
        } else {
            money -= max10;
            cash[0] += max10;
            have[0] -= max10;
        }

        if (money % 5 != 0) {
            int ooi10 = 5 - (money % 5);
            money += ooi10;
            if (cash[0] != 0) {
                cash[0] -= ooi10;
                ret[0] = 1;
            }
            have[0] += ooi10;
        }

        int max50 = money / 5;
        if (max50 > have[1]) {
            money -= have[1] * 5;
            cash[1] += have[1];
            have[1] -= have[1];
        } else {
            money -= max50 * 5;
            cash[1] += max50;
            have[1] -= max50;
        }

        if (money % 10 == 5) {
            money += 5;
            if (cash[1] != 0) {
                cash[1] -= 1;
                ret[1] = 1;
            }
            have[1] += 1;
        }

        money /= 10;
        int max100 = money;
        if (max100 > have[2]) {
            money -= have[2];
            cash[2] += have[2];
            have[2] -= have[2];
        } else {
            money -= max100;
            cash[2] += max100;
            have[2] -= max100;
        }

        if (money % 5 != 0) {
            int ooi100 = 5 - (money % 5);
            money += ooi100;
            if (cash[2] != 0) {
                cash[2] -= ooi100;
                ret[2] = 1;
            }
            have[2] += ooi100;
        }

        cash[3] = money / 5;
        have[3] -= cash[3];

        if (ret[0] == 0 && have[0] >= 5) {
            if (ret[2] == 0) {
                cash[0] += (have[0] / 10) * 10;
            }
            if (ret[1] == 0) {
                cash[1] += (have[0] / 5) * 5;
            }
        } 
        if (ret[1] == 0 && have[1] >= 2) {
            if (ret[2] == 0) {
                cash[2] += (have[1] / 2) * 2;
            }
        } 
        if (ret[2] == 0 && have[2] >= 5) {
            cash[2] += (have[2] / 5) * 5;
        } 


        output(cash);
        cin >> money;
        if (money == 0) {
            break;
        }
        cout << endl;
    }
    return 0;
