using namespace std;

typedef vector<int> sequence;

sequence shop(int value) {
  sequence seq(4);
  seq[0] = value / 500; value -= 500 * seq[0];
  seq[1] = value / 100; value -= 100 * seq[1];
  seq[2] = value / 50; value -= 50 * seq[2];
  seq[3] = value / 10;
  return seq;
}

int main() {
  int value;
  bool flag = false;
  while (cin >> value and value) {
    if (flag) { cout << endl; } else { flag = true; }
    sequence seq(4);
    REP(i, 4) {
      cin >> seq[3-i];
    }
    int total = seq[0]*500 + seq[1]*100 + seq[2]*50 + seq[3]*10;
    sequence ret = shop(total - value);

    int values[4] = {500, 100, 50, 10};

    for (int i = 3; i >= 0; i--) {
      int coins = max(0, seq[i] - ret[i]);
      if (coins) {
        cout << values[i] << " " << coins << endl;
      }
    }
  }
}

