int main() {
    cin.tie(nullptr);
    ios::sync_with_stdio(false);
    const int C[4] = {10, 50, 100, 500};
    int N; bool init = true;
    while(cin >> N && N) {
        if(init) init = false;
        else cout << '\n';
        vector<int> num(4);
        for(int& x : num) cin >> x;
        int rest = 0;
        for(int i : in(4)) rest += C[i] * num[i];
        rest -= N;
        vector<int> ans(4, 0);
        for(int i : in(3, -1)) {
            int amari = rest / C[i];
            rest -= amari * C[i];
            ans[i] = max(0, num[i] - amari);
        }
        for(int i : in(4))
            if(ans[i])
                cout << C[i] << ' ' << ans[i] << '\n';
    }

    return 0;
}

