    for(int i = 3; i >= 0; i--){
      if(s >= b[i]){
	k[i] += s / b[i];
	s -= k[i] * b[i];
      }
    }

    for(int i = 0, f = 0; i < 4; i++, f = 0){
      if(k[i]-c[i] <= 0){
	r[i] = k[i];
