void Solve(int n){
  int result=100;
  int result_s,result_t,result_u,result_v;
  int a,b,c,d;
  cin>>a>>b>>c>>d;
  for(int ai=0;ai<=a;ai++){
    for(int bi=0;bi<=b;bi++){
      for(int ci=0;ci<=c;ci++){
        for(int di=0;di<=d;di++){

          int paid=ai*10+bi*50+ci*100+di*500;
          if(paid>=n){

            int s=a-ai,t=b-bi,u=c-ci,v=d-di;
            int change=paid-n;
            bool invalid=false;
            while(change>=500){
              if(di>0){
                invalid=true;
                break;
              }
              change-=500;
              v++;
            }
            if(invalid) continue;

            while(change>=100){
              if(ci>0){
                invalid=true;
                break;
              }
              change-=100;
              u++;
            }
            if(invalid) continue;

            while(change>=50){
              if(bi>0){
                invalid=true;
                break;
              }
              change-=50;
              t++;
            }
            if(invalid) continue;

            while(change>=10){
              if(ai>0){
                invalid=true;
                break;
              }
              change-=10;
              s++;
            }
            if(invalid) continue;

            if(s+t+u+v<result){
              result=s+t+u+v;
              result_s=ai;
              result_t=bi;
              result_u=ci;
              result_v=di;
            }

          }

        }
      }
    }
  }
  if(result_s){
    cout<<10<<" "<<result_s<<endl;
  }
  if(result_t){
    cout<<50<<" "<<result_t<<endl;
  }
  if(result_u){
    cout<<100<<" "<<result_u<<endl;
  }
  if(result_v){
    cout<<500<<" "<<result_v<<endl;
  }

}

int main(void) {
  cout << std::fixed << std::setprecision(10);
  cin.tie(0);
  std::ios::sync_with_stdio(false);

  while(true){
    int n;
    cin>>n;
    if(n==0){
      break;
    }

    Solve(n);
    cout<<endl;
  }

  return 0;
}

