#define REP(i, a, n) for(int i = (a); i < (n); i++)
using namespace std;

int N, A, B, C, D;

int main(void) {
  bool first = true;
  while(cin >> N, N) {
    cin >> A >> B >> C >> D;

    int aa = 0, ab = 0, ac = 0, ad = 0, n = A + B + C + D;
    REP(a, 0, A + 1) REP(b, 0, B + 1) REP(c, 0, C + 1) REP(d, 0, D + 1) {
      int s = a * 10 + b * 50 + c * 100 + d * 500;
      if(s >= N) {
        int change = s - N;

        int chd = change / 500;
        change -= chd * 500;
        int chc = change / 100;
        change -= chc * 100;
        int chb = change / 50;
        change -= chb * 50;
        int cha = change / 10;
        change -= cha * 10;

        int ka = A - a + cha;
        int kb = B - b + chb;
        int kc = C - c + chc;
        int kd = D - d + chd;

        if(n >= ka + kb + kc + kd) {
          aa = a, ab = b, ac = c, ad = d;
          n = ka + kb + kc + kd;
        }
      }
    }

    if(!first) cout << endl;
    first = false;

    if(aa > 0) cout << "10 " << aa << endl;
    if(ab > 0) cout << "50 " << ab << endl;
    if(ac > 0) cout << "100 " << ac << endl;
    if(ad > 0) cout << "500 " << ad << endl;
  }

  return 0;
}

