    f = true;
    ans = INF;
    vector<int> v(T), used(T, 0);
    REP(i, T) cin >>v[i];
    set< vector<int> > S;
    dfs(v, used, 0, S);
    REP(i, T) if(ans_v[i]) cout <<c[i] <<" " <<ans_v[i] <<endl;
  }
  return 0;
}

