void solve() {
  int mmm = 0;
  while (true) {
    int n; cin >> n;
    if (n == 0) return;
    if (mmm++ > 0) cout << '\n';
    int x, y, z, w; cin >> x >> y >> z >> w;
    int ans = 1e9;
    int a, b, c, d;
    repc(i, x) repc(j, y) repc(k, z) repc(l, w) {
      int sum = i * 10 + j * 50 + k * 100 + l * 500;
      if (sum < n) continue;
      sum -= n;
      int cnt = x - i + y - j + z - k + w - l;
      if (l > 0 && sum / 500 > 0) continue; cnt += sum / 500; sum %= 500;
      if (k > 0 && sum / 100 > 0) continue; cnt += sum / 100; sum %= 100;
      if (j > 0 && sum /  50 > 0) continue; cnt += sum /  50; sum %=  50;
      if (i > 0 && sum /  10 > 0) continue; cnt += sum /  10;
      if (ans >= cnt) {
        ans = cnt;
        a = i, b = j, c = k, d = l;
      }
    }
    if (a != 0) cout <<  10 << " " << a << '\n';   
    if (b != 0) cout <<  50 << " " << b << '\n';   
    if (c != 0) cout << 100 << " " << c << '\n';   
    if (d != 0) cout << 500 << " " << d << '\n';   
  }
}

