#include <iostream>
#include <cmath>
using namespace std;

int main() {
	const int c[4] = {10, 50, 100, 500};
	const int cc[3] = {5, 2, 5};
	int p, n[4], k[4];
	while (cin >> p, p) {
		for (int i = 0; i < 4; i++) {
			cin >> n[i];
		}
		for (int i = 3, q = p; i >= 0; i--) {
			k[i] = q / c[i];
			q %= c[i];
		}
		for (int i = 0; i < 3; i++) {
			if (k[i] < n[i]) {
				int e = min(k[i + 1], (n[i] - k[i]) / cc[i]);
				k[i] += e * cc[i];
				k[i + 1] -= e;
			}
		}
		for (int i = 0; i < 3; i++) {
			if (k[i] > n[i]) {
				int e = min(k[i] - n[i], ((k[i] - n[i] - 1) / cc[i] + 1) * cc[i]);
				k[i] -= e;
				k[i + 1] += (e - 1) / cc[i] + 1;
			}
		}
		for (int i = 0; i < 4; i++) {
			if (k[i]) {
				cout << c[i] << ' ' << k[i] << endl;
			}
		}
		cout << endl;
	}
	return 0;
}

