vin solve(int y,int t,int fif,int h,int fiv){
    vin ans(4,0);
    ans[0]=t;ans[1]=fif;ans[2]=h;ans[3]=fiv;
    int r=10*t+50*fif+100*h+500*fiv-y;
    ans[3]-=r/500;r-=(r/500)*500;
    ans[2]-=r/100;r-=(r/100)*100;
    ans[1]-=r/50;r-=(r/50)*50;
    ans[0]-=r/10;
    return ans;
}

int main(){
    queue<vin> q;
    while(1){
        int yen,ten,fifty,hund,fivehund;
        cin>>yen;
        if(yen==0)break;
        cin>>ten>>fifty>>hund>>fivehund;
        q.push(solve(yen,ten,fifty,hund,fivehund));
    }
    while(q.size()){
