void circumcenter(double x1, double y1, double x2, double y2, double x3, double y3, double res[3]){
    double xp1 = (y1-y3)*(y1*y1-y2*y2+x1*x1-x2*x2) - (y1-y2)*(y1*y1-y3*y3+x1*x1-x3*x3);
    double xp2 = 2*(y1-y3)*(x1-x2)-2*(y1-y2)*(x1-x3);
    res[0] = xp1/xp2;
    double yp1 = (x1-x3)*(x1*x1-x2*x2+y1*y1-y2*y2) - (x1-x2)*(x1*x1-x3*x3+y1*y1-y3*y3);
    double yp2 = 2*(x1-x3)*(y1-y2)-2*(x1-x2)*(y1-y3);
    res[1] = yp1/yp2;
    double r = (x3-res[0])*(x3-res[0])+(y3-res[1])*(y3-res[1]);
    res[2] = sqrt(r);
}



class BinaryIndexedTree{
public:
    int n; vector<int> bit;
    BinaryIndexedTree(int _n){
        n = _n;
        bit.resize(n+1,0);
    }
    int sum(int i){
        int sum = 0;
        while(i > 0){
            sum += bit[i];
            i -= i & -i;
        }
        return sum;
    }
    void add(int i, int v){
        while(i <= n){
            bit[i] += v;
            i += i & -i;
        }
    }
};


class UnionFindTree{
public:
    vector<int> parent, rank;
    int n;
    int cnt;
    // 
    UnionFindTree(int _n){
        n = _n;
        parent.resize(n);
        rank.resize(n);
        for(int i = 0; i < n; i++){
            parent[i] = i;
            rank[i] = 0;
        }
        cnt = n;
    }
    // 
    int find(int x){
        if(parent[x] == x){
            return x;
        }else{
            return parent[x] = find(parent[x]);
        }
    }
    // xy
    void unite(int x, int y){
        x = find(x);
        y = find(y);
        if(x == y){
            // set.insert(x);
            return;
        }
        if(rank[x] < rank[y]){
            parent[x] = y;
            cnt--;
        }else{
            parent[y] = x;
            if(rank[x] == rank[y]) rank[x]++;
            cnt--;
        }
    }
    // xy
    bool same(int x, int y){
        return find(x) == find(y);
    }
};



