int turi(int n,int i,int j,int k,int l){
	int sum=0;
	if(n<0)return -1;
	while(n>=500){if(l!=0)return -1;n-=500;sum++;}
	while(n>=100){if(k!=0)return -1;n-=100;sum++;}
	while(n>=50){if(j!=0)return -1;n-=50;sum++;}
	while(n>=10){if(i!=0)return -1;n-=10;sum++;}
	return sum;
}
