int cn[4] = {10,50,100,500};
int cc[4];
int ca[4];

int getcc(int aa){
  int res=0;
  for(int i=3;i>=0;i--){
    cc[i]=aa/cn[i];
    aa%=cn[i];
    res+=cc[i];
  }
  return res;
}

int main(){
  int c[4];
  int ans;
  int n;
  cin>>n;
  while(1){
    ans = 1000;
    for(int i=0;i<4;i++)cin>>c[i];

    for(int x=0;x<=c[0];x++)
      for(int y=0;y<=c[1];y++)
        for(int z=0;z<=c[2];z++)
          for(int w=0;w<=c[3];w++){
            int m=x*cn[0]+y*cn[1]+z*cn[2]+w*cn[3];
            if(m<n) continue;
            int mc = getcc(m-n);
            if((cc[0]&&x)||(cc[1]&&y)||(cc[2]&&z)||(cc[3]&&w)) continue;
            int cs=c[0]-x+c[1]-y+c[2]-z+c[3]-w;
            if(ans>mc+cs){
